import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io.connect('http://localhost:3001');

function App() {

  const [message, setMessage] = useState("");
  const [backenddenGelenMesaj, setBackenddenGelenMesaj] = useState("");
  const [qrup, setQrup] = useState("");
  const [name, setName] = useState("");

  const eventGonder = ()=>{
    socket.emit('backende_mesaj', {message, qrup, name})

  }

  const join_qrup = ()=>{
    socket.emit('join_qrup', qrup)
  }

  useEffect(()=>{
    socket.on("frontende_mesaj",(data)=>{
      setBackenddenGelenMesaj(`${data.name}: ${data.message}`)
    })
  }, [socket])

  return (
    <div className="App">
      <div className="fromContainer">
        <div className="header">
        <h1 className="chatHeader">MadManDev Chat App</h1>
        </div>
        <div className="groupInfor">
        <input onChange={(e)=>{setName(e.target.value)}} placeHolder="Adinizi daxil edin"></input>
        <input className="groupName" onChange={(e)=>{setQrup(e.target.value)}} placeHolder="Qrupun adini yazin"></input>
      <button className="joinToGroup" onClick={join_qrup}>Qrupa daxil ol</button>
      </div>

    <div className=''></div>
      <input className="message" onChange={(e)=>{setMessage(e.target.value)}} placeHolder="Mesajinizi yazin"></input>
      <button className="sendMessage" onClick={eventGonder}>Mesaji gonder</button>


          <h1>{backenddenGelenMesaj}</h1>

          </div>
    </div>
  );
}

export default App;
