import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io.connect('http://localhost:3001');

function App() {
  const [message, setMessage] = useState("");
  const [backenddenGelenMesaj, setBackenddenGelenMesaj] = useState("");
  const [qrup, setQrup] = useState("");
  const [name, setName] = useState("");

  const eventGonder = ()=>{
    socket.emit('backende_mesaj', {message, qrup, name})

  }

  const join_qrup = ()=>{
    socket.emit('join_qrup', qrup)
  }

  useEffect(()=>{
    socket.on("frontende_mesaj",(data)=>{
      setBackenddenGelenMesaj(`${data.name}: ${data.message}`)
    })
  }, [socket])


  return (
    <div id="loginform">
        <h2 id="headerTitle">MadManDev Çat Proqramı</h2>
        <div class="row">
          <label>Adni</label>
          <input type="text" placeHolder="Adınızı daxil edin"/>
        </div>
        <div class="row">
          <label>Otaq</label>
          <input type="password" placeHolder="Otaq kodunu daxil edin"/>
        </div>
        <div id="button" class="row">
    <button>{props.title}</button>
  </div>  
        <div class="row">
          <label>Mesaj</label>
          <input type="text" placeHolder="Mesajınızı yazın..."/>
        </div>    
        <OtherMethods />
      </div>
  );
}

const FormHeader = props => (
  <h2 id="headerTitle">{props.title}</h2>
);

const Form = props => (
 <div>
   <FormInput onChange={(e)=>{this.setName(e.target.value)}} placeholder="Adını yaz" type="text" />
   <FormInput onChange={(e)=>{this.setQrup(e.target.value)}} placeholder="Otağın kodunu yaz" type="text"/>
   <OtagaDaxilOLButton onClick={this.join_qrup} title="Otağa daxil ol"/>
   <FormInput onChange={(e)=>{this.setMessage(e.target.value)}} placeholder="Mesajını yaz..." type="text"/>
   <GonderButton onClick={this.eventGonder} title="Mesajı göndər"/>
 </div>
);

const OtagaDaxilOLButton = props => (
  <div id="button" class="row">
    <button>{props.title}</button>
  </div>
  );

const GonderButton = props => (
<div id="button" class="row">
  <button>{props.title}</button>
</div>
);

const FormInput = props => (
<div class="row">
  <label>{props.description}</label>
  <input type={props.type} placeholder={props.placeholder}/>
</div>  
);

const OtherMethods = props => (
<div id="alternativeLogin">
  <label>Or sign in with:</label>
  <div id="iconGroup">
    <Facebook />
    <Twitter />
    <Google />
  </div>
</div>
);

const Facebook = props => (
<a href="#" id="facebookIcon"></a>
);

const Twitter = props => (
<a href="#" id="twitterIcon"></a>
);

const Google = props => (
<a href="#" id="googleIcon"></a>
);

export default App;
