import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io.connect('http://localhost:3001');

function App() {

  const [message, setMessage] = useState("");
  const [backenddenGelenMesaj, setBackenddenGelenMesaj] = useState("");

  const eventGonder = ()=>{
    socket.emit('backende_mesaj', {message})

  }

  useEffect(()=>{
    socket.on("frontende_mesaj",(data)=>{
      setBackenddenGelenMesaj(data.message)
    })
  }, [socket])

  return (
    <div className="App">
      <h1>MadManDev Chat App</h1>

      <input onChange={(e)=>{setMessage(e.target.value)}} placeHolder="Mesajinizi yazin"></input>
      <button onClick={eventGonder}>Mesaji gonder</button>
      <h3>{backenddenGelenMesaj}</h3>
    </div>
  );
}

export default App;
