import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io.connect('http://localhost:3001');

function App() {

  const [message, setMessage] = useState("");
  const [backenddenGelenMesaj, setBackenddenGelenMesaj] = useState("");
  const [qrup, setQrup] = useState("");
  const [name, setName] = useState("");

  const eventGonder = ()=>{
    socket.emit('backende_mesaj', {message, qrup, name})

  }

  const join_qrup = ()=>{
    socket.emit('join_qrup', qrup)
  }

  useEffect(()=>{
    socket.on("frontende_mesaj",(data)=>{
      setBackenddenGelenMesaj(`${data.name}: ${data.message}`)
    })
  }, [socket])

  return (
    <div className="App">

<>
  <p>
    Wrap a pair of{' '}
    <code>
      {`<Input>`}
    </code>
    {' '}and{' '}
    <code>
      {`<Label>`}
    </code>
    {' '}components in{' '}
    <code>
      {`<FormGroup floating>`}
    </code>
    {' '}to enable floating labels with Bootstrapâ€™s textual form fields. A{' '}
    <code>
      placeholder
    </code>
    {' '}is required on each{' '}
    <code>
      {`<Input>`}
    </code>
    {' '}as our method of CSS-only floating labels uses the{' '}
    <code>
      :placeholder-shown
    </code>
    {' '}pseudo-element. Also note that the{' '}
    <code>
      {`<Input>`}
    </code>
    {' '}must come first so we can utilize a sibling selector (e.g.,{' '}
    <code>
      ~
    </code>
    ).
  </p>
  <Form>
    <FormGroup floating>
      <Input
        id="exampleEmail"
        name="email"
        placeholder="Email"
        type="email"
      />
      <Label for="exampleEmail">
        Email
      </Label>
    </FormGroup>
    {' '}
    <FormGroup floating>
      <Input
        id="examplePassword"
        name="password"
        placeholder="Password"
        type="password"
      />
      <Label for="examplePassword">
        Password
      </Label>
    </FormGroup>
    {' '}
    <Button>
      Submit
    </Button>
  </Form>
</>


