import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io.connect('http://localhost:3001');

function App() {

  const eventGonder = ()=>{
    socket.emit('backende_mesaj', {message: 'salam'})

  }

  useEffect(()=>{
    socket.on("frontende_mesaj",(data)=>{
      alert(data.message)
    })
  }, [socket])

  return (
    <div className="App">
      <h1>MadManDev Chat App</h1>
      <input placeHolder="Mesajinizi yazin"></input>
      <button onClick={eventGonder}>Mesaji gonder</button>
    </div>
  );
}

export default App;
